import React, { useContext, useEffect, useRef } from "react";
// CONTEXT
import { ProjectProvider } from "../context/ProjectsContext";
// STYLES
import "../App.css";
import Typed from "typed.js";

// FUNCTIONS
function Hero() {
  const { doodleImg, artImg } = useContext(ProjectProvider);
  const el = useRef(null);

  useEffect(() => {
    const typed = new Typed(el.current, {
      strings: ["Handy", "Mandy", "Candy", "More Strings"], // Strings to display
      // Speed settings, try diffrent values untill you get good results
      startDelay: 300,
      typeSpeed: 100,
      backSpeed: 100,
      backDelay: 100,
    });

    // Destropying
    return () => {
      typed.destroy();
    };
  }, []);

  return (
    <div>
      <section class="home" id="home">
        <div class="logo-nav">
          <div class="logo">
            <div class="logo-line">
              <div class="line-1"></div>
              <div class="line-2"></div>
            </div>
            <h1>NEHL</h1>
          </div>
        </div>

        <div class="homeContent">
          <div class="self">
            <div class="textCon">
              <div class="text-content">
                <h2 class="autoType"  ref={el}> </h2>
              </div>
            </div>

            <div class="myImage">
              <img src={doodleImg} class="imgDoodle" alt="doodle" />
              <img src={artImg} alt="web developer" class="my-self" />
            </div>
          </div>

          <div class="homeSocials">
            <a href="https://instagram.com/nehlzin" title="instagram">
              <i class="fa-brands fa-instagram"></i>
            </a>
            <a href="https://twitter.com/nehlzin" title="twitter">
              <i class="fa-brands fa-twitter"></i>
            </a>
            <a href="https://dribbble.com/nehlzin" title="dribbble">
              <i class="fa-brands fa-dribbble"></i>
            </a>
            <a href="https://github.com/nehlzin" title="github">
              <i class="fa-brands fa-gitlab"></i>
            </a>
          </div>
        </div>
      </section>
    </div>
  );
}

export default Hero;
